export default (req, res) => {
  console.log(req.headers.x - auth - token);
  if (req.headers.x - auth - token) {
    res.statusCode = 200;
    res.setHeader("Content-Type", "application/json");
    res.json({
      authenticated: true,
    });
  } else {
    res.statusCode = 403;
    res.setHeader("Content-Type", "application/json");
    res.json({
      authenticated: false,
    });
  }
};
